setInterval(() => { // æ¯éš”1ç§’æ‰§è¡Œä¸€æ¬¡å‡½æ•°
    let create_time = Math.round(new Date('2025-01-25 00:00:00').getTime() / 1000); // è®¾ç½®å»ºç«™æ—¶é—´
    let timestamp = Math.round((new Date().getTime()) / 1000); // è·å–å½“å‰æ—¶é—´æˆ³
    let second = timestamp - create_time; // è®¡ç®—ä»å»ºç«™åˆ°ç°åœ¨ç»è¿‡çš„ç§’æ•°
    let time = new Array(0, 0, 0, 0, 0); // åˆå§‹åŒ–æ—¶é—´æ•°ç»„ï¼Œç”¨äºå­˜å‚¨å¹´ã€å¤©ã€æ—¶ã€åˆ†ã€ç§’

    var nol = function(h){ // å®šä¹‰ä¸€ä¸ªå‡½æ•°ï¼Œç”¨äºåœ¨æ•°å­—å°äº10æ—¶è¡¥é›¶
      return h>9?h:'0'+h;
    }

    // è®¡ç®—ç»è¿‡çš„æ—¶é—´
    if (second >= 365 * 24 * 3600) { // å¦‚æœè¶…è¿‡1å¹´
      time[0] = parseInt(second / (365 * 24 * 3600)); // è®¡ç®—å¹´æ•°
      second %= 365 * 24 * 3600; // å–ä½™æ•°
    }
    if (second >= 24 * 3600) { // å¦‚æœè¶…è¿‡1å¤©
      time[1] = parseInt(second / (24 * 3600)); // è®¡ç®—å¤©æ•°
      second %= 24 * 3600; // å–ä½™æ•°
    }
    if (second >= 3600) { // å¦‚æœè¶…è¿‡1å°æ—¶
      time[2] = nol(parseInt(second / 3600)); // è®¡ç®—å°æ—¶æ•°ï¼Œå¹¶è¡¥é›¶
      second %= 3600; // å–ä½™æ•°
    }
    if (second >= 60) { // å¦‚æœè¶…è¿‡1åˆ†é’Ÿ
      time[3] = nol(parseInt(second / 60)); // è®¡ç®—åˆ†é’Ÿæ•°ï¼Œå¹¶è¡¥é›¶
      second %= 60; // å–ä½™æ•°
    }
    if (second > 0) { // å¦‚æœè¿˜æœ‰ç§’æ•°
      time[4] = nol(second); // è¡¥é›¶
    }

    // æ ¹æ®æ—¶é—´åˆ¤æ–­æ˜¾ç¤ºå†…å®¹
    if ((Number(time[2])<22) && (Number(time[2])>7)){ // å¦‚æœå½“å‰æ—¶é—´åœ¨7ç‚¹åˆ°22ç‚¹ä¹‹é—´
      currentTimeHtml =`<img class='boardsign' src='https://img.shields.io/badge/dmwå°å±‹-è¥ä¸šä¸­-6adea8?style=social&logo=cakephp' title='æ¬¢è¿å…‰ä¸´~'>` +
        `<div id='runtime'>` + 'æœ¬ç«™å·²ç»è¿è¡Œ' + time[0] + ' å¹´ ' + time[1] + ' å¤© ' + time[2] + ' : ' + time[3] + ' : ' + time[4] + 'äº†å“Ÿ~' + '</div>';
    }
    else{ // å¦åˆ™
      currentTimeHtml =`<img class='boardsign' src='https://img.shields.io/badge/dmwå°å±‹-æ‰“çƒŠäº†-6adea8?style=social&logo=coffeescript' title='è¿™ä¸ªç‚¹äº†åº”è¯¥å»ç¡è§‰å•¦ï¼Œç†¬å¤œå¯¹èº«ä½“ä¸å¥½å“¦'>` +
        `<div id='runtime'>` + time[0] + ' YEAR ' + time[1] + ' DAYS ' + time[2] + ' : ' + time[3] + ' : ' + time[4] + '</div>';
    }

    // å°†ç”Ÿæˆçš„HTMLå†…å®¹æ’å…¥åˆ°é¡µé¢ä¸­
